import { useLoader } from '@react-three/fiber';
import { FBXLoader } from 'three-stdlib';

function useFBX(path) {
  return useLoader(FBXLoader, path);
}

useFBX.preload = path => useLoader.preload(FBXLoader, path);

export { useFBX };
