import { useState, useEffect } from 'react';
import { getGPUTier } from 'detect-gpu';

function useDetectGPU(props) {
  const [GPUTier, setGPUTier] = useState(null);
  useEffect(() => {
    getGPUTier(props).then(result => setGPUTier(result));
  }, [props]);
  return GPUTier;
}

export { useDetectGPU };
